import React from 'react';
import OverlayScrollbars from 'overlayscrollbars';
import Card from 'react-bootstrap/Card';

export default class Tut2 extends React.Component {
    async componentDidMount(){
        await(10);
        var osInstance = OverlayScrollbars(document.body);
        osInstance.scroll({ top : 0 });
    }

    render() {
        return (
            <div className="text-dark text-wrap" style={{minHeight: '100vh', marginBottom: '200px'}}>
                <h1>How to make a functioning tickbox</h1>
                <p className="mt-4 TutText">
                    First you want to start a new react base app by using npx create-react-app.
                </p>
                <p className="mt-4 TutText">
                    Modify your index.js file to look like this
                </p>
                <div style="background: #ffffff; overflow:auto;width:auto;border:solid gray;border-width:.1em .1em .1em .8em;padding:.2em .6em;"><pre style="margin: 0; line-height: 125%"><span style="color: #008800; font-weight: bold">import</span> React from <span style="background-color: #fff0f0">&#39;react&#39;</span>;
<span style="color: #008800; font-weight: bold">import</span> ReactDOM from <span style="background-color: #fff0f0">&#39;react-dom&#39;</span>;
<span style="color: #008800; font-weight: bold">import</span> App from <span style="background-color: #fff0f0">&#39;./App&#39;</span>;
<span style="color: #008800; font-weight: bold">import</span> <span style="color: #333333">*</span> as serviceWorker from <span style="background-color: #fff0f0">&#39;./serviceWorker&#39;</span>;
 
ReactDOM.render(<span style="color: #333333">&lt;</span>App <span style="color: #333333">/&gt;</span>,<span style="color: #007020">document</span>.getElementById(<span style="background-color: #fff0f0">&#39;root&#39;</span>)
);
 
<span style="color: #888888">// If you want your app to work offline and load faster, you can change</span>
<span style="color: #888888">// unregister() to register() below. Note this comes with some pitfalls.</span>
<span style="color: #888888">// Learn more about service workers: https://bit.ly/CRA-PWA</span>
serviceWorker.unregister();
</pre></div>


            </div>
        )
    }
}
